  Vector(int _x = 0, int _y = 0) { x = _x, y = _y; }
//  Vector operator+(const Vector& rhs) const { return Vector(x + rhs.x, y + rhs.y); }
  Vector operator-(const Vector& rhs) const { return Vector(x - rhs.x, y - rhs.y); }
  double operator^(const Vector& rhs) const { return x * rhs.y - y * rhs.x; }
//  double dis() { return sqrt(x * x + y * y); }
  bool operator<(const Vector& rhs) const { return x == rhs.x ? y < rhs.y : x < rhs.x; }
};

Vector rtt(Vector x, double tht) {
  return Vector(x.x * cos(tht) - x.y * sin(tht), x.x * sin(tht) + x.y * cos(tht));
}
vector<Vector> p;

signed main() {
  // code begin.
  in >> n;
  for (int i = 1; i <= n; i++) in >> a[i].x >> a[i].y, p.push_back({a[i].x, a[i].y});
  int sz = p.size() - 1;
  sort(p.begin(), p.end());
  static int st[233333], top = 0;
  st[++top] = 0, st[++top] = 1;
  auto ins = [&](int x) {
    while (top >= 2 && ((p[st[top - 1]] - p[st[top]]) ^ (p[st[top]] - p[x])) <= 0) --top;
    st[++top] = x;
  };
  for (int i = 2; i <= sz; i++) {
    ins(i);
  }
  int cnt = 0;
  for(int i = 1 ; i <= top ; i ++) a[++ cnt] = { p[st[i]].x, p[st[i]].y };
  st[top = 1] = sz, st[++top] = sz - 1;
  for (int i = sz - 2; ~i; --i) {
    ins(i);
  }
	for(int i = 2 ; i <= top ; i ++) a[++ cnt] = { p[st[i]].x, p[st[i]].y };
  for (int i = 1; i <= cnt; i++) a[i + cnt] = a[i];
  solve(1, cnt, 1, cnt * 2);
  int res = 0;
  for (int i = 1; i <= cnt; i++) cmax(res, dis(a[i], a[ans[i]]));
  out << res << '\n';
  return 0;
  // code end.
}
```

我们充分发扬人类智慧：

将大部分点按照 $x$ 坐标从小到大排序，小部分点按照 $y$ 坐标从大到小排序。

根据数学直觉，在排序后，最远的两个点在数组中肯定不会离得太近。

所以只取最后的 $12000$ 个点更新最远距离。

这样代码短的吃惊，多交上几遍就能够过了。

```cpp
#include <bits/stdc++.h>
#define int long long
using namespace std;
const int N = 2e5 + 10;
struct Node
{
  int x, y;
  bool operator<(const Node &rhs) const
  {
    return rand() % 25 == 0 ? x < rhs.x : y > rhs.y;
  }
} z[N];
int dis(int x, int y)
{
  return (z[y].x - z[x].x) * (z[y].x - z[x].x) + (z[y].y - z[x].y) * (z[y].y - z[x].y);
}
signed main()
{
  srand(time(0));
  int n;
  cin >> n;
  for (int i = 0; i < n; i++)
    cin >> z[i].x >> z[i].y;
  sort(z, z + n);
  int mx = -1e18;
  for (int i = 0; i < n; i++)
    for (int j = n - 1, k = 0; j > i && k < 12000; j--, k++)
      mx = max(mx, dis(i, j));
  printf("%d\n", mx);
  return 0;
}
