#include<bits/stdc++.h>
using namespace std;
int dx[12]={0,0,1,-1,1,1,-1,-1,2,-2,0,0},//增量值：移动数组
    dy[12]={1,-1,0,0,1,-1,1,-1,0,0,2,-2};
bool g[110][110];
int n,m,ans;
int q[10010][2];
void bfs(int x,int y)
{
    int h=0,t=1;//头尾赋值
    q[1][0]=x;q[1][1]=y;
    g[x][y]=0;
    while(h<t)
    {
        h++;
        x=q[h][0],y=q[h][1];
        for(int i=0;i<12;i++)
        {
            int x1=x+dx[i],y1=y+dy[i];
            if(x1>=1&&x1<=n&&y1>=1&&y1<=m&&g[x1][y1])//正向思路
            {
                t++;
                q[t][0]=x1;
                q[t][1]=y1;
                g[x1][y1]=0;
            }
        }
    }
}
int main()
{
    cin>>n>>m;
    for(int i=1;i<=n;i++)
      for(int j=1;j<=m;j++)
      {
      	char c;
      	cin>>c;
      	if(c=='#')g[i][j]=1;//判断
      }
      for(int i=1;i<=n;i++)
       for(int j=1;j<=m;j++)
       if(g[i][j])
       {
       	++ans;//其实就是搜索联联  通块
       	bfs(i,j);
       }
       cout<<ans;
    return 0;
}
