#include<cstdio>
#include<vector>
#include<cstring>
#include<algorithm>
const int MAXN = 10000 + 6;

int v[MAXN],c[MAXN],V[MAXN],C[MAXN],f[MAXN],curCC;
std::vector<int> G[MAXN];
bool hash[MAXN];
void PREVISIT(int u){
	V[curCC] += v[u];
	C[curCC] += c[u];
}
void dfs(int u){
	hash[u] = true;
	PREVISIT(u);
	for(int i = 0;i < G[u].size();i++){
		if(!hash[G[u][i]]) dfs(G[u][i]);
	}
}
void findCC(int n){
	curCC = 0;
	for(int u = 0;u < n;u++) if(!hash[u]){curCC++;dfs(u);}
}

int main(){
	int n,m,w;scanf("%d %d %d",&n,&m,&w);
	for(int i = 0;i < n;i++) scanf("%d %d",&v[i],&c[i]);

	for(int i = 0;i < m;i++){
		int x,y;
		scanf("%d %d",&x,&y);x--;y--;
		G[x].push_back(y);G[y].push_back(x);
	}
	findCC(n);
	for(int i = 1;i <= curCC;i++){
		for(int j = w;j >= V[i];j--){
			f[j] = std::max(f[j],f[j - V[i]] + C[i]);
		}
	}
	printf("%d",f[w]);

	return 0;
}

```

本题是并查集+背包，很好的题目，其实就是把搭配的物品（并查集合并）看成一个物品，需要根据并查集的结果，把价格和价值都累加起来，有多少个分支点就有多少个物品，合并后物品的数量变少了，根据题意， n和w <10000，所以二维背包肯定卡内存，我把物品由n个缩成了num个，这num 就是分支点，然后直接0-1背包就好了。
本题的重点：并查集的每一个元素x的父节点a[x]并不一定是指向祖先的，只有当find(x)后x的父节点才指向祖先，我在这里卡了一会；例如两个分支点A和B合并后，B挂在A的下面，处于分支点B 的叶子节点的父节点是不会指向A 的祖先的。要find后才可以的。
```cpp
#include <iostream>
using namespace std;
int t[10001],v[10001],a[10001],t_h[10001],v_h[10001],t1[10001],v1[10001];
int dp[10001];
int fd(int a[],int x)
{
    if (x!=a[x])
        a[x]=fd(a,a[x]);
    return a[x];
}
int join(int x,int y)
{
    int x1=fd(a,x);
    int y1=fd(a,y);
    if (x1!=y1)
    {
        a[y1]=x1;
       return 1;
    }
    return 0;

}
int main()
{
    int n,m,w,a1,b1;
    cin>>n>>m>>w;
    for(int i=1;i<=n;i++)
        {cin>>t[i]>>v[i];
         a[i]=i;
        }
    for(int i=1;i<=m;i++)
    {  cin>>a1>>b1;
        join(a1,b1);
    }
    //下面找唯一的祖先，然后把价格和价值都累加上。
    for(int i=1;i<=n;i++)
    {
        t_h[fd(a,i)]=t_h[a[i]]+t[i];//这句很关键，不是每个节点的父亲都是指向祖先的!
        v_h[fd(a,i)]=v_h[a[i]]+v[i];// 在2个子树合并后，叶子节点的父亲并没有指向新祖先！
        //t_h[a[i]]这样写是错误的。
    }
    //下面开始缩点
    int num=0;
    for(int i=1;i<=n;i++)
    {
        if (t_h[i]>0)
        {   num++;
            t1[num]=t_h[i];
            v1[num]=v_h[i];
            //num++;
        }
    }

    for(int i=1;i<=n;i++)
        for(int j=w;j>=t1[i];j--)
            dp[j]=max(dp[j],dp[j-t1[i]]+v1[i]);
   cout<<dp[w]<<endl;



    return 0;
}

```

### 花了一个小时调这个题~~~
#### 思路很明显! 强连通分量+缩点+背包~~~
```cpp
#include <cstdio>
#include <cstring>
#include <cstdlib>

#include <iostream>
