int n,m,w,f[10005],money[10005],v[10005],ans(0),dp[10005],used[10005],k(0);
int ww[10005],mm[10005];
int find(int x)
{
    if(f[x]==x)return x;
    return find(f[x]);
}
void UN(int x,int y)
{
    int a=find(x);
    int b=find(y);
    f[a]=b;
    money[b]+=money[a];//先将每组搭配购买的物品的总花费、总价值算出来
    v[b]+=v[a];
}
void solve()
{
    memset(used,0,sizeof(used));
