void dfs(int x , int num ,long long  mul){
	if( x > 12 ) return ;// 这里是因为 2 * 3 * 5 * ...... * 29 *31 > 2 * 10^ 9
	//超过了题目范围  
	if(num > last || (num == last && mul < ans)){
	 //约数多  或者 数小更新答案 
      ans = mul;
      last = num;
	 }
     s[x] = 0;//每次变成零 
     while(mul * prime[x] <= n  && s[x]  < s[x - 1]){//满足指数单调递减 
     	mul *= prime[x];
     	s[x] ++;//约数指数++ 
     	dfs(x + 1 , num * (s[x] + 1 ) , mul)  ;// 算数基本定理的推论一个数的约数个数 = （ci为指数）
		 (c1 + 1) * (c2 + 1) ....* (Cm + 1)... 
	 }
}
int main () {
