```cpp
int check(int x)/*检测因子个数*/
{
    int sum=0;
    for(int i=2;i<=int(sqrt(x));i++)
    if(x%i==0)
    {
        if(i*i==x)sum++;
        else sum+=2;
    }
    return sum+2;
}
void dfs(int k)/*搜索反质数*/
{
    for(int i=1;i<=20;i++)
    if(pri[i]*k<=n)
    {
        int sum=check(pri[i]*k);
        if(vis[sum]==0||vis[sum]>pri[i]*k)
        {
            vis[sum]=pri[i]*k;
            maxv=max(maxv,sum);
            dfs(pri[i]*k);
        }
    }
}
