	}//以上是高精度除以单精度的过程 
	for (int i=1;i<=l;i++)
	{
		if (f==0 && a[i]>0) f=1;//避免前导0 
		if (f==1) putchar(a[i]+'0');//输出(也可以直接printf("%d",a[i]);) 
	}
	return 0;//结束了 
