int main()
{
    int a,b,c,d;
    char x;
    cin>>a>>x>>b >>c>>x>>d;//x防止“/”的输入
    a*=c;b*=d;
    //之后c和d就没用了
    c=a;d=b;
    //更相减损法
    while(c!=d)
    {
        if(c>d)
            c-=d;
        else
            d-=c;
    }

    int ww=d;//此时ww为最大公因数
    a/=ww;b/=ww;
    cout<<b<<" "<<a<<endl;//注意是先列再行
}
