     */
    void show(){
        if(this -> getDen() < 0){
            this -> setDen(-this -> getDen());
            this -> setNum(-this -> getNum());
        }

        if(this -> den == 1){
            printf("%d",this -> num);
        }else{
            printf("%d/%d",this -> num,this -> den);
        }
