```cpp
#include <stdio.h>
#include <iostream>
#define N 1000001
using namespace std;
int n,m,a[N],bit[N],s;
inline int lowbit(int x)
{
	return x&-x;
}
inline void update(int x,int k)//单点更改 
{
	while(x<=n)
	{
		bit[x]+=k;
		x+=lowbit(x);
	}
}
inline int query(int k)
{
	int i(0);
	while(k>0)
	{
		i+=bit[k];
		k-=lowbit(k);
	}
	return i;
}
signed main()
{
	ios::sync_with_stdio(false);
	cin.tie(0);
	cout.tie(0);
	register int i,j,k;
	cin>>n>>m;
	for(i=1;i<=n;i++)
	{
		cin>>a[i];
		update(i,a[i]);
	}
	for(i=1;i<=m;i++)
	{
		int C,x,y;
		cin>>C>>x;
		if(C==1)
		{
			cin>>y;
			for(k=1;k*x<=n;k++)//枚举k 
			{
				update(x*k,y);
			}
		}
		if(C==2)
		{
			cout<<query(x)-query(x-1)<<endl;//区间查 
		}
	}
	return 0;;
}
