    if(x > 9) print(x / 10);
    putchar(x % 10 + '0');
}
int n,m,a[2000005],ans,MAX;
priority_queue<pair<int,int> > pq;
bool vis[2000005];
signed main () {
	read(n);read(m);
	for (int i = 1 ; i <= n ; ++ i)
		read(a[i]),pq.push(make_pair(a[i],i));
	while (m --) {
		pair<int,int> no = pq.top();
		pq.pop();
		while (vis[no.second]) {
			no = pq.top();
			pq.pop();
		}
		ans += no.first;
		if (MAX > ans) return 0 & printf("%lld",MAX);
