			vis[n] = 1;
			vis[2] = 1;
		}
		if (no.second == n) {
			vis[1] = 1;
			vis[n - 1] = 1;
		}
		else vis[no.second + 1] = vis[no.second - 1] = 1;
	}
	printf("%lld",ans);
	return 0;
}
