        for(int j=0;j<3;j++)
            scanf("%d",&h[i].a[j]);
    for(int j=0;j<3;++j)
        point[h[0].a[j]]=true;
    short ans=0;
    for(int i=1;i<n-2;++i){
        int _ans=0;
        for(int j=0;j<3;++j)
            _ans+=point[h[i].a[j]];
        if(_ans==2) ++ans;
    }
    if(ans==1)  puts("JMcat Win");
    else if(ans==2){
        if(n%2) puts("PZ Win");
        else puts("JMcat Win");
    }
    else{
        if(n%2) puts("PZ Win");
        else puts("JMcat Win");
    }
    return 0;
