              {
              dp[p+h[i]][s][k+1]=1;//方程
              if(k>0)
              dp[p][s+h[i]][k-1]=1;
              else
              dp[s+h[i]][p][1]=1;
              }
          }
    }
    for(int i=sum;i>=0;i--)
        {
            if(dp[i][sum-i][0]||dp[i][sum-i][1])
            {
                ans=Min(ans,abs(i-sum+i));
            }
        }
//        cout<<ans;
    for(int i=sum;i>=0;i--)//输出最小值
        {
            if(dp[i][sum-i][0]||dp[i][sum-i][1])
//            printf("%d,%d,%d %d   ",i,sum-i,dp[i][sum-i][0],dp[i][sum-i][1]);
            if(abs(i-sum+i)==ans)
