bool cmp(point a,point b){return pos[a.l]==pos[b.l] ? a.r<b.r : pos[a.l]<pos[b.l];}
void add(int ind){
	nowans+=count[ind]<<1;
	nowans++;
    count[ind]++;
}
void del(int ind){
