#define ll long long
#include<algorithm>
#include<iostream>
#include<cstdio>
#define N 100010
#include<cmath>
using namespace std;
struct node
{
    ll id,l,r,z,m;
}ask[N];
ll num[200010],n,block;
ll a[N],q,tot;
bool comp(node aa,node bb)
{
    if(aa.l/block==bb.l/block)
    {
        return aa.r<bb.r;
    }
    return aa.l/block<bb.l/block;
}
bool cmp(node aa,node bb)
{
    return aa.id<bb.id;
}
void add(ll nu)
{
    ll pa=num[nu];
    num[nu]++;
    ll no=num[nu];
    tot-=pa*(pa-1)/2;
    tot+=no*(no-1)/2;
}
void pop(ll nu)
{
    ll pa=num[nu];
    num[nu]--;
    ll no=num[nu];
    tot-=pa*(pa-1)/2;
    tot+=no*(no-1)/2;
}
ll gcd(ll a,ll b)
{
    return b==0?a:gcd(b,a%b);
}
int main()
{
    scanf("%lld",&n);
    scanf("%lld",&q);
    block=sqrt(n);
    for(ll i=1;i<=n;++i) scanf("%lld",&a[i]);
    for(ll i=1;i<=q;++i)
    {
        scanf("%lld%lld",&ask[i].l,&ask[i].r);
        ask[i].id=i;
    }
    sort(ask+1,ask+q+1,comp);
    for(ll i=ask[1].l;i<=ask[1].r;++i) add(a[i]);
    ask[1].z=tot;
    ll s=(ask[1].r-ask[1].l+1);
    ask[1].m=s*(s-1)/2;
    if(ask[1].z==0) ask[1].m=1;
    else 
    {
        ll it=gcd(ask[1].z,ask[1].m);
        ask[1].z/=it;
        ask[1].m/=it;
    }
    ll L=ask[1].l,R=ask[1].r;
    for(ll i=2;i<=q;++i)
    {
        while(R<ask[i].r) add(a[++R]);
        while(R>ask[i].r) pop(a[R--]);
        while(L>ask[i].l) add(a[--L]);
        while(L<ask[i].l) pop(a[L++]);
        ask[i].z=tot;
        ll s=(R-L+1);
        ask[i].m=s*(s-1)/2;
        if(ask[i].z==0) ask[i].m=1;
        else 
        {
            ll it=gcd(ask[i].z,ask[i].m);
            ask[i].z/=it;
            ask[i].m/=it;
        }
    }
    sort(ask+1,ask+q+1,cmp);
    for(ll i=1;i<=q;++i)
    printf("%lld/%lld\n",ask[i].z,ask[i].m);
    return 0;
}
