program ex1496;
var
  a:array[1..20000,1..2] of longint;
  i,j,k,m,n,s,l,r:longint;
  t:int64;

procedure qs(l,r:longint);
var
  i,j,s,mid:longint;
begin
  i:=l;
  j:=r;
