      a[j,1]:=s;
      s:=a[i,2];
      a[i,2]:=a[j,2];
      a[j,2]:=s;
      inc(i);
      dec(j);
    end;
  until i>j;
  if l<j then qs(l,j);
  if i<r then qs(i,r);
end;

begin
  readln(n);
  for i:=1 to n do
    readln(a[i,1],a[i,2]);
  qs(1,n);
  l:=a[1,1];
