```cpp
#include<cstdio>
#include<iostream>
#include<cstring>
#include<algorithm>
#include<cmath>
using namespace std;
int n,m,tp,ans=0;
struct edge
{
    int l;
    int r;
};
struct edge E[20001];
int cmp(edge a,edge b) //按起点大小排序 
{
    return a.l<b.l;
}
void work()
{
    E[0].r=-0x3f3f3f3f; 
    int la=0; //上一次处理的区间 
    for(int i=1;i<=n;i++)
    {
        if(E[i].r<=E[la].r+tp)  //如果现在要处理的区间被上一次的包含，就跳过 
            continue;
        if(E[i].l<=E[la].r+tp) E[i].l=E[la].r+tp+1; //部分包含的情况 
        la=i;
        if(((E[i].r-E[i].l+1)%1)==0) tp=0;//计算区间长度 
        else tp=1-((E[i].r-E[i].l+1)%1);//这些地方我写了个%1 ，/1之类的东西 
        if(tp==0) ans+=(E[i].r-E[i].l+1)/1;//看上去蛮鬼畜的，其实没用，和不加一样 
        else ans+=(E[i].r-E[i].l+1)/1+1;//这是因为我这个题是直接改的P1589的代码 
	}//为了省事本来是%m /m的全改成1了，大家不要被误导了qaq
}
int main()
{
    scanf("%d",&n);
    for(int i=1;i<=n;i++)
    {
        scanf("%d%d",&E[i].l,&E[i].r);
        E[i].r--;
    }
    sort(E+1,E+1+n,cmp);
    work();
    printf("%d",ans);
    return 0;
}
