const int MAX = 1e9 + 10;

const int N = 2e4 + 10;

int t[N ] , tag[N ] , root , idx , n;
int ls[N ] , rs[N ];

void pushdown(int x , int l , int r) {
	if(!ls[x]) ls[x] = ++ idx;
	if(!rs[x]) rs[x] = ++ idx; 
	tag[ls[x]] = 1;
	tag[rs[x]] = 1;
	int mid = l + r >> 1;
