    a[i*2-1]:=x;
    b[i*2-1]:=1;
    a[i*2]:=y;
    b[i*2]:=-1;
  end;
  qsort(1,2*n);//排序

  i:=1; t:=b[1]; j:=1;//初始化 t代表当前i到j的状态之和
  while i<2*n do
  begin
    inc(j);
    inc(t,b[j]);//加上这一坐标的状态
    while a[j]=a[j+1] do 
    begin inc(j); inc(t,b[j]); end;//如果有多个起点和终点在同一坐标，就可以直接处理所有的状态，不会产生重复长度对答案的影响
    if t=0 then begin inc(ans,a[j]-a[i]); i:=j+1; inc(t,b[i]); j:=j+1; end;//状态为0，加上长度，此时下一个坐标的状态必为1，直接把i赋值为下一个即可
  end;

  writeln(ans);
end.
