			tut[i][j]=tut[i-num][j-num];
	num<<=1;
}
void OUTPUT()
{
	for(int i=num-1;i>=0;i--)
	{
		for(int j=0;j<2*num;j++)
			cout<<tut[i][j];
		cout<<endl;
	}
}
