            h:=h+s;
      read(s);
    end;
    if h<min then min:=h;
  end;
  for i:=min downto 1 do
    if g[i]=n then begin write(i);halt;end;
  write(0);
end.
一种类似于打标记的算法，不过用背包算法写程序
