void lx/*dfs*/(int k)//k:第k个旅馆 
{
	//所有旅馆都走过了[满足条件] 
	if(k==n){
		//方法+1 
		ans++;
		return;
	}
	//从第k个旅馆出发 
	for(int i=k+1;i<=n;i++)
	//(当日?)所走路程满足条件{A<=路程<=B} 
	   if(a[i]-a[k]>=A&&a[i]-a[k]<=B/*可以走*/)
	      lx(i);//去第i个旅馆
}
int main()
{
	cin>>A>>B>>n;
	//[输入]新增的旅馆(距起点)路程 
	for(int i=1;i<=n;i++)
	    cin>>a[13+i];
	//把新增的旅馆数和本有的旅馆数加起来(方便使用!) 
	n=n+13;
