int ans[14+21];//记录到达每个旅馆的方案数 
int main()
{
    int a,b,n,i,j,k;
    scanf("%d %d %d",&a,&b,&n);
    i=0;
    map[i++] = 0;
    map[i++] = 990;
    map[i++] = 1010;
    map[i++] = 1970;
    map[i++] = 2030;
    map[i++] = 2940;
    map[i++] = 3060;
