    map[i++] = 4060;
    map[i++] = 4970;
    map[i++] = 5030;
    map[i++] = 5990;
    map[i++] = 6010;
    map[i++] = 7000;
    while(n--)scanf("%d",&map[i++]);
    sort(map,map+i);//将新增的旅馆位置从小到大排序 
    ans[0] = 1;//第一个旅馆的方案数为1 
    for(j=0;j<i;j++)
    {
