    d1=dis(p[n].x,p[n].y,p[n-1].x,p[n-1].y),d2=dis(p[1].x,p[1].y,p[2].x,p[2].y);
	for(ri i=1;i<=n-2;i++)
        f[n-1][i]=dis(p[n].x,p[n].y,p[i].x,p[i].y)+d1;
