    for(int i = 1; i <= n; i++)
    	for(int j = i + 1; j <= n; j++)
    		d[i][j] = dis(e[i], e[j]), f[i][j] = 1e30;
    f[1][2] = d[1][2];
    for(int i = 1; i <= n; i++)
    	for(int j = i + 1; j <= n; j++)
    	{
    		f[i][j + 1] = min(f[i][j + 1], f[i][j] + d[j][j + 1]);
    		f[j][j + 1] = min(f[j][j + 1], f[i][j] + d[i][j + 1]);
		}
	for(int i = 1; i < n; i++)
		ans = min(ans, f[i][n] + d[i][n]);
