        {
           if(a[j+2][i]==0)
           {num++;father[num]=num;a[j+2][i]=num;}
           if(a[j+1][i+1]==0)
           {num++;father[num]=num;a[j+1][i+1]=num;}
           val[a[j+2][i]]+=2*k-3;
           val[a[j+1][i+1]]+=2*k-3;
           fi=find(a[j+2][i]);
           fj=find(a[j+1][i+1]);
           if(fi!=fj)  {father[fj]=fi;sum++;}
        }
        if(s[k][i][j]==92||s[k][i][j]=='X')
        {
            if(a[j+1][i]==0)
            {num++;father[num]=num;a[j+1][i]=num;}
            if(a[j+2][i+1]==0)
            {num++;father[num]=num;a[j+2][i+1]=num;}
            val[a[j+1][i]]+=2*k-3;
            val[a[j+2][i+1]]+=2*k-3;
            fi=find(a[j+1][i]);
            fj=find(a[j+2][i+1]);
            if(fi!=fj) {father[fj]=fi;sum++;}
        }
    }
    sum=0;
    for(i=1;i<=num;i++)
    {
        fi=find(i);
        if(vist[fi]==0)
        {sum++;c[sum]=fi;vist[fi]=1;}
        b[fi]=b[fi]+abs(val[i]);
    }
    ans=0;
    for(i=1;i<=sum;i++)
    {
        if(b[c[i]]==0)  b[c[i]]=1;
       ans=ans+(b[c[i]]+1)/2;
   }
