#include<cstdio>
#include<map>
#include<algorithm>
#include<cmath>
using namespace std;
typedef long long ll;
ll Q;
int De[100];
map<int,ll> dft;
map<ll,int> idft;
bool cmp(int a,int b){ return a>b; }
ll Cha(ll x){
	ll res=0,Up=1,f=0,res2=0;
	while(x) De[++f]=x%10,x/=10; //把数拆分 
	sort(De+1,De+f+1,cmp); //排序…… 
	for(int i=1;i<=f;i++){
		res+=Up*De[i]; //最小数 
		res2+=Up*De[f-i+1]; //最大数 
		Up=(Up<<3)+(Up<<1); // 就是Up=Up*10了 
	}
	return res2-res;
}
signed main()
{
	while(scanf("%lld",&Q)!=EOF){
		int top=0; //编号 
		dft.clear(); 
		idft.clear(); //每次做完把map清空 
		while(!idft[Q]){ //额，看这个数是否出现，一直做下去。 
			idft[Q]=++top; //Q -> top 
			dft[top]=Q; //top -> Q
			Q=abs(Cha(Q)); //求下一个Q 
		}
		for(int i=idft[Q];i<=top;i++) //idft到top正好就是一组答案 
		printf("%lld ",dft[i]);
