int p;
int sum=0;//记录总价值
bool f[MAXT]={true};
int x=1;//记录回合
int main(){
	while(1){
		sum=0;
		for(int i=1;i<MAXT;i++)
			f[i]=false;
		//初始化
		for(int i=1;i<=n;i++){
			cin>>p;
			sum+=p*i;//价值总和
			for(int j=0;j<i;j++){
				int q=0;//记录可用弹珠价值为i的个数
				for(int k=j;k<=sum;k+=i){
					if(f[k]==true)
						q=p;//全部又可以用了
					else if(q>0){
						q--;//用去1个
						f[k]=true;
					}
				}
			}
		}
		if(sum==0)
		    break;//出口
		if(sum%2==1||f[sum/2]==false)
