	if(a[c-'0'][1])	for(int i=1;i<=k;i++) scrn[i][r+w-2]='|';
	if(a[c-'0'][2]) for(int i=k+2;i<h-1;i++) scrn[i][r+w-2]='|';
	if(a[c-'0'][3]) for(int i=r+1;i<r+w-2;i++) scrn[k+k+2][i]='-';
	if(a[c-'0'][4])	for(int i=k+2;i<h-1;i++) scrn[i][r]='|';
	if(a[c-'0'][5]) for(int i=1;i<=k;i++) scrn[i][r]='|';
