    for(int i=1;i<=placenum;i++)
    {
        scanf("%llf%llf%llf",&times[i],&timee[i],&dis[i]);
    }
    R=2000000000;  //此处为了保证不出错，把初始速度定的大一些。不要定太大，否则容易TLE
    while(R-L>0.000000001)  //保证精度
    {
        k=(L+R)/2;
        if(speedtest(k))
            R=k;
