bool test(double a){
    double t = 0;//t指的是前一个目的地，最快用当前速度a可以什么时候到
    for(int i = 1; i <= n; i++){
        if (arr[i].s > (arr[i].y - t) * a) return false; //如果我前一个地方连现在这个地方的最晚时间都到不了，就false
        t = std::max(arr[i].x, t + arr[i].s / a);//很关键，如果从上一个t连这个目的地的x都到不了，那t就是x，否则就是t + arr[i].s/a
    }
