int main()
{
	ios::sync_with_stdio(false);
	int n, m;
	cin >> n >> m;
	int jz(0), jc(0); //分别计正方形、长方形的个数 
	for (int i = 0; i <= n; i++)
		for (int j = 0; j <= m; j++)
		//模拟，一个一个地暴力枚举 
			for (int l1 = i + 1; l1 <= n; l1++)
				for (int l2 = j + 1; l2 <= m; l2++)
				{
					int e1 = l1 - i;
					int e2 = l2 - j;
