        for(int i=2;i<=n*n;i++){
            if(used[i]==1) continue ;
            if(x==1&&prime[f[x][y-1]+i]==0) continue ;
            if(y==1&&prime[f[x-1][y]+i]==0) continue ;
            f[x][y]=i;used[i]=1;
