        if(P==test[i])return 1;
        LL a=Pow(test[i],t,P),nxt=a;
        for(int j=1;j<=k;j++){
            nxt=(a*a)%P;
            if(nxt==1&&a!=1&&a!=P-1)return 0;
            a=nxt;
        }
        if(a!=1)return 0;
    }
    return 1;
}
