```cpp
#include<bits/stdc++.h>
using namespace std;
int n,m,p,father[5200];
//n，m，p的解释见题，father数组用来记录该结点属于哪个集合（存储根结点/父亲结点）
struct node
{
    int x,y;
}a[5200];
//a数组用来存储每对亲戚关系。
int getfather(int k)
{
    if(father[k]==k)return k;
    //如果一个点的父亲是自己，那么它就是根结点
    father[k]=getfather(father[k]);
    //这里是记忆化，压缩路径。
    return father[k];
}
//getfather就是找到该结点的根结点，并记忆
void bing(int k,int l)
{
    int fx,fy;
    fx=getfather(k);
    fy=getfather(l);
    father[fx]=fy;
}
//并操作。对两个点的根结点进行如下操作：一个根结点变成另外一个点的儿子结点。那么两个集合就并为一个集合了。
bool cha(int k,int l)
{
    int fx,fy;
    fx=getfather(k);
    fy=getfather(l);
    return fx==fy;
}
//查操作。求出两个结点的根结点，如果相同，那么他们属于一个集合。
void read()
{
    scanf("%d%d%d",&n,&m,&p);
    int x,y;
    for(int i=1;i<=n;i++)
    father[i]=i;
    //每个结点的根结点默认是自己（这就可以理解为有n个集合，每个点是一个集合）
    for(int i=1;i<=m;i++)
    {
        scanf("%d%d",&x,&y);
        bing(x,y);
        //读入亲戚关系后，并他。
    }
}
//读入
void work()
{
    int x,y;
    for(int i=1;i<=p;i++)
    {
        scanf("%d%d",&x,&y);
        if(cha(x,y))printf("Yes\n");
        else printf("No\n");
        //读入后查询
    }
}
int main()
{
    read();//读入
    work();//工作
    return 0;
}
