    if pas[c]=c then exit(c);
    pas[c]:=getfather(pas[c]);
    exit(pas[c]);
end;
procedure union(a,b:longint);
begin
    x:=getfather(a);
    y:=getfather(b);
    if x<>y then pas[x]:=y;
