	}
	if(s!=-1){
		int m = s-1;                                                               //类似 
		for(;a[m]=='0'&&m!=0;--m)     a[m]=97;
		for(int m =strlen(a)-1;a[m]=='0'&&m!=s+1;--m)     a[m]=97;
	}
	if(d!=-1){
		for(int i = d-1;i!=-1;--i){         //反转输出 ，是零跳过 
			if(a[i]==97)    continue;
		    cout<<a[i];
		}
		cout<<'.';
		for(int i = strlen(a)-1;i!=d;--i){
			if(a[i]==97)    continue;
		    cout<<a[i];
	    }
	}
	else if(s!=-1){
		for(int i = s-1;i!=-1;--i){
			if(a[i]==97)    continue;
		    cout<<a[i];
