#include<iostream>
#include<cstdio>
#include<cstring>
#include<algorithm>
#define lli long long int
#define debug cout
using namespace std;
const int maxn=15;
lli f[maxn][maxn][maxn];
lli ans[maxn];
lli pw[maxn];
inline void gen()
{
    pw[1]=1;
    for(int i=2;i<14;i++)
        pw[i] = pw[i-1] * 10LL;
    for(int i=0;i<10;i++)
        f[1][i][i] = 1;
    for(int len=2;len<14;len++)
        for(int first=0;first<10;first++)
            for(int count=0;count<10;count++)
            {
                for(int from=0;from<10;from++)
                    f[len][first][count] += f[len-1][from][count];
                if(count==first) f[len][first][count] += pw[len];
            }
}
inline void getans(lli num,lli lam)
{
    if(!num)
    {
        ans[0] += lam;
        return;
    }
    static int dat[maxn],len;
    lli bak = num;
    memset(dat,0,sizeof(dat));
    len=0;
    while(num)
        dat[++len] = num%10,
        num /= 10;
    for(int i=1;i<len;i++) // i is the length
        for(int k=0;k<10;k++) // k is the counting number
            for(int x=1;x<10;x++) // x is the first number
                ans[k] += f[i][x][k] * lam;
    for(int t=len;t;t--)
    {
        for(int first=0;first<dat[t];first++)
        {
            if(t==len&&!first) continue;
            for(int k=0;k<10;k++) // k is the counting number
            {
                ans[k] += f[t][first][k] * lam;
            }
        }
        ans[dat[t]] += (bak%pw[t]+1) * lam;
    }
}
int main()
{
    static lli a,b;
    gen();
    scanf("%lld%lld",&a,&b);
    if(a-1) getans(a-1,-1);
    getans(b,1);
    for(int i=0;i<10;i++)
    {
        printf("%lld",ans[i]);
        if(i!=9) putchar(' ');
        else putchar('\n');
    }
    return 0;
}
```

