```cpp
#include <iostream>

using namespace std;

long long res[2][10];

void fun()
{
    for (int j = 0; j < 2; ++j) {
        long long x;
        cin >> x;
        x -= 1 - j;
        long long pow = 1;
        long long single = 0;
        long long tmp;
        int i = 0;
        long long all = 0;
        while ((tmp = x / pow) != 0) {
            int now = tmp % 10;
            for (int i = 1; i < 10; ++i) {
                res[j][i] += now * single;
                if (now > i) {
                    res[j][i] += pow;
                } else if (now == i) {
                    res[j][i] += x % pow + 1;
                }
            }
            all += i * 9 * pow;
            single = ++i * pow;
            pow *= 10;
        }
        all = 1 + all / 10 + (x - pow / 10 +1) * i;
        for (int i = 1; i < 10; ++i) {
            all -= res[j][i];
        }
        res[j][0] += all;
    }
}

void print()
{
    for (int i = 0; i < 10; ++i) {
        cout << res[1][i] - res[0][i] << " ";
    }
}

int main()
{
    fun();
    print();
    return 0;
}

```

