var
  a:array[0..9]of qword;                    //每个数字的统计变量
  n,m,j,k,s:qword;  i:longword;
begin
  readln(n,m);                              //读入
  fillchar(a,sizeof(a),0);                  //数组清零
  for i:=n to m do                          //所有数字一一循环
  begin
    k:=i;
    while k>0 do                            //这个循环用来分解数字
    begin
      inc(a[k mod 10]);
      k:=k div 10;
    end;
  end;
  for i:=0 to 9 do write(a[i],' ');         //输出
end.

