using namespace std;
char sss[100]; 
long long zh2(char x[100]){//二进制转十进制 
	long long ans=0;
	int t=1;
	int l=strlen(x);
	for(int i=l-1;i>=0;i--){
		ans+=(x[i]-'0')*t;
		t=t*2;
	}
	return ans;
}
void zh3(long long x){//十进制转换三进制 
	char ans[100];
    for(int i=0;;i++){
    	ans[i]=x%3+'0';
    	x/=3;
    	if(x==0){
    		ans[i+1] = '\0';
			break;
		}
	}
	int l=strlen(ans);
	int p=0;
	for(int i=l-1;i>=0;i--){
		sss[p]=ans[i];
		p++;
	}
	sss[p]='\0';
}
