string a;
int n;

int jinzhi(string k,int n)
{
    int ans=1,res=1;
    int len=a.length();//Calculate The Length Of 'a'
    for(int i=0;i<len;i++)
    {
        res=1;
        for(int j=1;j<=i;j++)
        {
            res*=n;
        }
        ans+=res*(a[len-i-1]-'0');//Calculate The Answer Of Each Position
    }
    return ans-1;//Return The Answer
}

