    c[e] = co ;
}
void Add ( int x, int y, int fl, int co ) {
    add ( x, y, fl, co ) ;
    add ( y, x, 0, -co ) ;
}

queue <int> Q ;
bool inq[maxn] ;
bool spfa ( int s, int t ) {
    int i, j, u, x = s ;
    for ( i = 1 ; i <= t ; i ++ )
        dis[i] = -zhf ;
    memset ( fa, 0, sizeof(fa) ) ;
    dis[x] = 0 ;
    lim[x] = zhf ;
    Q.push(x) ;
    inq[x] = 1 ;
    while ( !Q.empty() ) {
        x = Q.front() ;
        Q.pop() ;
        inq[x] = 0 ;
        for ( i = be[x] ; i ; i = nxt[i] ) {
            u = to[i] ;
            if ( dis[u] < dis[x]+c[i] && f[i]>0 ) {
                dis[u] = dis[x]+c[i] ;
                fa[u] = i ;
                lim[u] = min( lim[x], f[i] ) ;
                if ( !inq[u] ) {
                    inq[u] = 1 ;
                    Q.push(u) ;
                }
            }
        }
    }
    if ( dis[t]==-zhf ) return 0 ;
    for ( x = fa[t] ; x ; x = fa[to[x^1]] ) {
        f[x] -= lim[t] ; f[x^1] += lim[t] ;
        cost += lim[t]*c[x] ;
    }
    return 1 ;
}

int main() {
    fread( buff, 1, maxN, stdin ) ;
    int i, j, k, x, y, s, t ;
    Read(n) ; s = n<<1|1, t = s+1 ;
    for ( k = 0 ; k < 2 ; k ++ )
        for ( i = 1 ; i <= n ; i ++ )
            for ( j = 1 ; j <= n ; j ++ )
                Read(a[i][j][k]) ;
    for ( i = 1 ; i <= n ; i ++ ) {
        Add ( s, i, 1, 0 ) ;
        Add ( i+n, t, 1, 0 ) ;
        for ( j = 1 ; j <= n ; j ++ )
            Add ( i, j+n, 1, a[i][j][0]*a[j][i][1]) ;
    }
    while ( spfa(s,t) ) ;
    printf ( "%d\n", cost ) ;
    return 0 ;
}
