int n,ans,a[22][22],b[22][22],f[22];bool c[22];
inline int max(int x,int y){return x>y?x:y;}
inline void sch(int t,int x){
	if(t>n) ans=max(ans,x);//更新答案
	else{
		RI s=0;
		for(RI i=t;i<=n;i=-~i) s+=f[i];
		if(x+s<=ans) return;//剪枝
		for(RI i=1;i<=n;i=-~i)
			if(!c[i]){
				c[i]=1;
				sch(-~t,x+a[t][i]);
				c[i]=0;//回溯
			}	
