		for(RI j=1;j<=n;j=-~j)
			a[i][j]*=b[j][i];//乘积预处理
	for(RI i=1;i<=n;i=-~i)
		for(RI j=1;j<=n;j=-~j)
			f[i]=max(f[i],a[i][j]);//局部最大值，搜索时剪枝用。
