```

剩下 dep 和 mask 两维，可以记忆化搜索了

时间复杂度 $O(n\times2^n)$，常数写小点就能过~

```cpp
// This code wrote by chtholly_micromaker(MicroMaker)
#include <bits/stdc++.h>
#define reg register
using namespace std;
template <class t> inline void read(t &s)
{
	s=0;
	reg int f=1;
	reg char c=getchar();
	while(!isdigit(c))
	{
		if(c=='-')
			f=-1;
		c=getchar();
	}
	while(isdigit(c))
		s=(s<<3)+(s<<1)+(c^48),c=getchar();
	s*=f;
	return;
}
int p[25][25],q[25][25];
int f[20][(1<<20)+5];
int n,ans;
inline int dfs(int dep,int mask)
{
	if(dep>=n)
		return 0;
	if(~f[dep][mask])
		return f[dep][mask];
	f[dep][mask]=0;
	for(int i=0;i<n;++i)
		if(!(mask&(1<<i)))
			f[dep][mask]=max(f[dep][mask],dfs(dep+1,mask|(1<<i))+p[dep][i]*q[i][dep]);
	return f[dep][mask];
}
signed main(void)
{
	memset(f,-1,sizeof f);
	cin>>n;
	for(int i=0;i<n;++i)
		for(int j=0;j<n;++j)
			read(p[i][j]);
	for(int i=0;i<n;++i)
		for(int j=0;j<n;++j)
			read(q[i][j]);
	cout<<dfs(0,0)<<endl;
	return 0;
}
