	for (register int i = 1; i <= n; i++)
		if(mp[k][i] == '*' && a[i] == 0 && b[i - k + n] == 0 && c[i + k] == 0){
			a[i] = b[i - k + n] = c[i + k] = 1;
			dfs(k + 1);
			a[i] = b[i - k + n] = c[i + k] = 0;
		}
}

